package gregtech.api.objects.elementalmatter;import java.util.HashMap;/*** Contains all isotopes data* Is loaded from Atoms.csv* */public class GT_AtomStats {    public static HashMap<Integer,GT_AtomStats> mAtoms = new HashMap<>(3000);    public String mName;    public double mMass, mHalfLife;    public short Z, N;    public float mNaturalAbundance;    public long mMagicNumber;    public String[] mDecayModes;    public float[] mDecayChances;    public GT_AtomStats(String aName, short aZ, short aN, double aMass, double aHalfLife, String[] aDecayModes, float[] aDecayChances, float aNaturalAbundance, long aMagicNumber){        mName = aName;        mAtoms.put(aZ<<16|aN,this);        Z = aZ;        N = aN;        mMass = aMass;        mHalfLife = aHalfLife;        mDecayModes = aDecayModes;        mDecayChances = aDecayChances;        mNaturalAbundance = aNaturalAbundance;        mMagicNumber = aMagicNumber;    }    public static GT_AtomStats getStats(short aZ, short aN){        int a= aZ<<16|aN;        return mAtoms.get(aZ<<16|aN);    }}